var arvato = arvato || {};
arvato.getItems = function (e) {
    $.ajax({
        url: e, type: "get", dataType: "json", beforeSend: function () {
            //$(".loading").show()
        }, success: function (e) {
            $(".loading").hide(), $(".container").show();
            for (var t = 0; t < e.items.length; t++) {
                var a = "", s = t + 1, i = '<dl class="survey-qa"><dt><span class="num">' + s + ".",
                    n = "</span><p>" + e.items[t].title + "</p></dt>", o = "", d = "", p = "";
                if ("input" == e.items[t].options_type) {
                    0 === e.items[t].options_refer ? p = '<div class="score-bar"><span>不太可能 <em>/ Unlikely</em></span><span>可能  <em>/ Likely</em></span></div>' : 1 === e.items[t].options_refer && (p = '<div class="score-bar"><span>差 <em>/ Poor</em></span><span>极好 <em>/ Excellent</em></span></div>');
                    o = "<dd>" + p + (e.items[t].options_do ? '<div class="score-choose need ' + e.items[t].type + '">' : '<div class="score-choose ' + e.items[t].type + '">');
                    for (var r = 0; r < e.items[t].options.length; r++) {
                        d += '<input id="' + e.items[t].options[r].id + '" name="v_' + e.items[t].id + '" type="' + e.items[t].type + '" value="' + e.items[t].options[r].id + '"><label for="' + e.items[t].options[r].id + '" >' + e.items[t].options[r].value + "</label>"
                    }
                }
                "text" == e.items[t].type && (o = '<dd class="textarea">', d = '<textarea name="v_' + e.items[t].id + '"></textarea>'), a = i + n + o + d + "</dd></dl>", "0" == e.type && $(".ajax-form").append(a)
            }
        }
    })
}, arvato.validate = function () {
    var e = !0, t = !0, a = !0;
    return $(".radio.need").each(function () {
        void 0 == $(this).find("input:checked").val() && (e = !1)
    }), $(".textarea").each(function () {
        "" == $(this).find("textarea").val() && (t = !1)
    }), $(".flex-box").each(function () {
        "" == $(this).find("input").val() && (a = !1)
    }), !!(e && t && a)
};